Geocode the Data

fields &lt;- definir_campos(
  estado = "federal_unit",
  municipio = "municipality_code",
  logradouro = "address",
  numero = "number",
  cep = "postal_code",
  localidade = "neighborhood"
)


establishment_data &lt;-
  establishment_data |&gt;
  geocode(
    campos_endereco = fields,
    resultado_sf = FALSE,
    verboso = TRUE,
    cache = TRUE,
    n_cores = 1,
    resolver_empates = TRUE
  )
