Download RFB CNPJ Data






See the Source of Data section for more information.



Clean Old Data Files

if (dir_exists(raw_data_dir)) {
  dir_delete(raw_data_dir)
  dir_create(raw_data_dir, recurse = TRUE)
}

Download Files

root &lt;- file.path( # Don´t change the function!
    "https://arquivos.receitafederal.gov.br",
    "dados",
    "cnpj",
    "dados_abertos_cnpj"
  )


path &lt;- root |&gt; file.path(paste0("2025-", str_pad(month, 2, pad = 0)))

urls &lt;-
  path |&gt;
  read_html() |&gt;
  html_elements("a") |&gt;
  html_attr("href") |&gt;
  str_subset("\\.zip$") %&gt;%
  file.path(path, .)


urls |&gt;
  map_dbl(.f = get_file_size, .progress = TRUE) |&gt;
  sum() |&gt;
  as_fs_bytes()
#&gt; 6.26G


urls |&gt; download_file(dir = raw_data_dir)

Unzip Files

zip_files &lt;- raw_data_dir |&gt; dir_ls(type = "file", regexp = "\\.zip$")


zip_files |&gt; map(\(x) unzip(x, exdir = raw_data_dir), .progress = TRUE)


zip_files |&gt; file_delete()

Save the Valid Data






The processed data are available in csv, rds and parquet formats through a dedicated repository on the Open Science Framework (OSF). See the Data Availability section for more information.



Clean Old Data Files

if (dir_exists(data_dir)) {
  dir_delete(data_dir)
  dir_create(data_dir, recurse = TRUE)
}

Write Data

valid_file_pattern &lt;- paste0(
  year,
  "-",
  str_pad(month, width = 2, pad = "0")
)


establishment_data |&gt;
  write_csv(
    here(data_dir, paste0(valid_file_pattern, ".csv"))
  )


establishment_data |&gt;
  write_rds(
    here(data_dir, paste0(valid_file_pattern, ".rds"))
  )


establishment_data |&gt;
  write_parquet(
    here(data_dir, paste0(valid_file_pattern, ".parquet"))
  )

Lock Data

here(data_dir, paste0(valid_file_pattern, ".csv")) |&gt;
  lock_file(
    public_key = public_key,
    suffix = ".lockr",
    remove_file = TRUE
  )


here(data_dir, paste0(valid_file_pattern, ".rds")) |&gt;
  lock_file(
    public_key = public_key,
    suffix = ".lockr",
    remove_file = TRUE
  )


here(data_dir, paste0(valid_file_pattern, ".parquet")) |&gt;
  lock_file(
    public_key = public_key,
    suffix = ".lockr",
    remove_file = TRUE
  )

Write Metadata

metadata_file_pattern &lt;- paste0(
  year,
  "-",
  str_pad(month, width = 2, pad = "0"),
  "-",
  "metadata"
)


metadata |&gt;
  write_csv(
    here("data", paste0(metadata_file_pattern, ".csv"))
  )


metadata |&gt;
  write_rds(
    here("data", paste0(metadata_file_pattern, ".rds"))
  )

Visualize the Data
Convert all TOM codes to their equivalent IBGE codes

municipalities &lt;-
  municipalities_tom_data |&gt;
  filter(municipality_code_tom %in% municipalities) |&gt;
  pull(municipality_code_ibge) |&gt;
  sort()

Plot the Data

for (i in municipalities) {
  # Set Shape -----

  shape &lt;-
    i |&gt;
    read_municipality(
      year = closest_geobr_year(year, verbose = FALSE),
      showProgress = FALSE
    ) |&gt;
    st_transform(st_crs(4326)) |&gt;
    suppressMessages()

  # Prepare Data -----

  plot_data &lt;-
    establishment_data |&gt;
    filter(municipality_code == i) |&gt;
    pivot_longer(
      cols = all_of(
        c("locaisnova_g0", "locaisnova_g1_g2", "locaisnova_g3", "locaisnova_g4")
      ),
      names_to = "group",
      values_to = "value"
    ) |&gt;
    filter(value == TRUE) |&gt;
    st_as_sf(
      coords = c("longitude", "latitude"),
      crs = 4326
    ) |&gt;
    st_join(shape, join = st_within, left = FALSE) %&gt;%
    mutate(
      latitude = st_coordinates(.)[,2],
      longitude = st_coordinates(.)[,1]
    ) |&gt;
    st_drop_geometry() |&gt;
    transmute(
      group =
        group |&gt;
        str_remove("locaisnova_") |&gt;
        str_to_upper() |&gt;
        str_replace("_", "-"),
      latitude,
      longitude
    )

  # Plot Data ---

  plot &lt;-
    ggplot() +
    geom_sf(
      data = shape,
      fill = "gray90",
      color = "black"
    ) +
    geom_point(
      data = plot_data,
      mapping = aes(x = longitude, y = latitude, color = group),
      size = 0.01
    ) +
    facet_wrap(~ group, ncol = 4) +
    coord_sf(crs = 4326) +
    scale_x_continuous(labels = NULL, breaks = NULL) +
    scale_y_continuous(labels = NULL, breaks = NULL) +
    scale_colour_brand_d() +
    theme(legend.position = "none") +
    labs(
      title = paste(
        "Locais-Nova Establishments in",
        municipalities_data |&gt;
          filter(municipality_code == i) |&gt;
          pull(municipality)
      ),
      x = NULL,
      y = NULL,
      color = NULL,
      caption =
        paste0(
          "Source: ",
          "Brazilian Federal Revenue Service (RFB), as of ",
          month.name[month], " ",
          year,
          "."
        )
    )

  print(plot)
}
